static (int Sum, int Count) Tally2(int[] values)
{
    var r = (s:0, c:0);
    foreach (var v in values)
    {
        //r = (r.s + v, r.c + 1); // 매번 계산을 하고 매번 할당이 된다.
        r.s += v; r.c++; // 직접 수정

